    private void cmdTambahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmdTambahActionPerformed
        // TODO add your handling code here:
        aktif(true);
        setTombol(false);
        kosong();
    }//GEN-LAST:event_cmdTambahActionPerformed

    private void cmdSimpanMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_cmdSimpanMouseClicked
        // TODO add your handling code here:
        int Kodekab = Integer.parseInt(txtKodeKab.getText());
        String tNmKab=txtNamaKab.getText();
        int Kuota=Integer.parseInt(txtKuota.getText());
        int JmlKec=Integer.parseInt(txtJmlKecamatan.getText());
        int DesaPen=Integer.parseInt(txtJmlDesa.getText());
        try{
            
            if (edit==true)
            {
                 stm.executeUpdate("update kabupaten set nama_kab='"+tNmKab+"',kuota="+Kuota+",jumlah_kecamatan="+JmlKec+",jumlah_desa_penempatan="+DesaPen+" where kode_kab=" +Kodekab+ "");
            }else                
            {
                stm.executeUpdate("INSERT INTO kabupaten VALUES('"+Kodekab+"','"+tNmKab+"','"+Kuota+"','"+JmlKec+"','"+DesaPen+"')");               
            }
            tblKab.setModel(new DefaultTableModel(dataTable,header));
            baca_data();
            aktif(false);
            setTombol(true);
        }catch(SQLException e) {
            JOptionPane.showMessageDialog(null, e);
            }
    }//GEN-LAST:event_cmdSimpanMouseClicked

