private void setField()
    {
        int row = tblKab.getSelectedRow();
        String kdKab = Integer.toString((Integer)tblKab.getValueAt(row, 0));
        txtKodeKab.setText(kdKab);
        txtNamaKab.setText((String)tblKab.getValueAt(row, 1));
        String kuotaa = Integer.toString((Integer)tblKab.getValueAt(row, 2));
        txtKuota.setText(kuotaa);
        String jmlKec = Integer.toString((Integer)tblKab.getValueAt(row, 3));
        txtJmlKecamatan.setText(jmlKec);
        String jmldes = Integer.toString((Integer)tblKab.getValueAt(row, 4));
        txtJmlDesa.setText(jmldes);
    }
    
    //method membuka database server, user, pass, database disesuaikan
    private void open_db()
    { try{
        KoneksiMysql kon = new KoneksiMysql("localhost","root","","uas_pkkp");
        Con = kon.getConnection();
    //System.out.println("Berhasil ");
    }catch (Exception e) {
        System.out.println("Error : "+e);
    }
    }
    
    private void baca_data()
    {
        try{
            stm = Con.createStatement(ResultSet.TYPE_SCROLL_SENSITIVE, ResultSet.CONCUR_READ_ONLY);
            RsKab = stm.executeQuery("SELECT * FROM kabupaten ORDER BY kode_kab");
            
            ResultSetMetaData meta = RsKab.getMetaData();
            int col = meta.getColumnCount();
            int baris = 0;
            while(RsKab.next()) {
                baris = RsKab.getRow();
            }
            dataTable = new Object[baris][col];
            int x = 0;
            RsKab.beforeFirst();
            while(RsKab.next()) {
                dataTable[x][0] = RsKab.getInt("kode_kab");
                dataTable[x][1] = RsKab.getString("nama_kab");
                dataTable[x][2] = RsKab.getInt("kuota");
                dataTable[x][3] = RsKab.getString("jumlah_kecamatan");
                dataTable[x][4] = RsKab.getString("jumlah_desa_penempatan");
                x++;
            }
            tblKab.setModel(new DefaultTableModel(dataTable,header));
        }
        catch(SQLException e)
        {
            JOptionPane.showMessageDialog(null, e);
        }
    }
    
    //untuk mengkosongkan isian data
    private void kosong()
    {
        txtKodeKab.setText("");
        txtNamaKab.setText("");
        txtKuota.setText("");
        txtJmlKecamatan.setText("");
        txtJmlDesa.setText("");
    }
    
    //mengset aktif tidak isian data
    private void aktif(boolean x)
    {
        txtKodeKab.setEditable(x);
        txtNamaKab.setEditable(x);
        txtKuota.setEditable(x);
        txtJmlKecamatan.setEditable(x);
        txtJmlDesa.setEditable(x);
    }
    
    //mengset tombol on/off
    private void setTombol(boolean t)
    {
        cmdTambah.setEnabled(t);
        cmdEdit.setEnabled(t);
        cmdHapus.setEnabled(t);
        cmdSimpan.setEnabled(!t);
        cmdBatal.setEnabled(!t);
        cmdKeluar.setEnabled(t);
    }
    
    
    
