    private void baca_data()
    {
        try{
            stm = Con.createStatement(ResultSet.TYPE_SCROLL_SENSITIVE, ResultSet.CONCUR_READ_ONLY);
            RsLolos = stm.executeQuery("SELECT k.nama_kab, k.kuota, t.no_seleksi, t.nama_lengkap, t.kab_penempatan, t.total_point FROM kabupaten k JOIN ( SELECT t.no_seleksi, t.nama_lengkap, t.kab_penempatan, t.total_point FROM ( SELECT t.no_seleksi, t.nama_lengkap, t.kab_penempatan, SUM(t.point + w.point) AS total_point, ROW_NUMBER() OVER (PARTITION BY t.kab_penempatan ORDER BY SUM(t.point + w.point) DESC) AS rn FROM tertulis t JOIN wawancara w ON t.no_seleksi = w.no_seleksi GROUP BY t.no_seleksi, t.nama_lengkap, t.kab_penempatan ) AS t WHERE t.rn <= ( SELECT kuota FROM kabupaten WHERE nama_kab = t.kab_penempatan ) ) AS t ON k.nama_kab = t.kab_penempatan ORDER BY k.nama_kab, t.total_point DESC;");
            
            ResultSetMetaData meta = RsLolos.getMetaData();
            int col = meta.getColumnCount();
            int baris = 0;
            while(RsLolos.next()) {
                baris = RsLolos.getRow();
            }
            dataTable = new Object[baris][col];
            int x = 0;
            RsLolos.beforeFirst();
            while(RsLolos.next()) {
                dataTable[x][0] = RsLolos.getString("nama_kab");
                dataTable[x][1] = RsLolos.getInt("kuota");
                dataTable[x][2] = RsLolos.getString("no_seleksi");
                dataTable[x][3] = RsLolos.getString("nama_lengkap");
                dataTable[x][4] = RsLolos.getString("kab_penempatan");
                dataTable[x][5] = RsLolos.getInt("total_point");
                x++;
            }
            tblLolos.setModel(new DefaultTableModel(dataTable,header));
        }
        catch(SQLException e)
        {
            JOptionPane.showMessageDialog(null, e);
        }
    }
    
