    private void cmdSimpanMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_cmdSimpanMouseClicked
        // TODO add your handling code here:
        String tNik=txtNik.getText();
        String tKabAsal=txtKabAsal.getText();
        String tKabPen=txtKabPenempatan.getText();
        String tNamaLengkap=txtNamaLengkap.getText();
        String tTTL = txtTTL.getText();
        String tAlamat = txtAlamat.getText();
        String tNoTelp = txtNoTelp.getText();
        String tEmaill = txtEmaill.getText();
        String tUniversitas = txtUniversitas.getText();
        String tFak = txtFak.getText();
        String tJur = txtJurusan.getText();
        String tThnLulus = txtThnLulus.getText();
        double ipk =Double.parseDouble(txtIpk.getText());
        try{
            
            if (edit==true)
            {
                 stm.executeUpdate("update peserta set kab_asal='"+tKabAsal+"',kab_penempatan='"+tKabPen+"',nama_lengkap='"+tNamaLengkap+"',ttl='"+tTTL+"',jenis_kelamin='"+sJenisKelamin+"', alamat ='"+tAlamat+"',agama = '"+sAgama+"', no_telp = '"+tNoTelp+"', email= '"+tEmaill+"', universitas ='"+tUniversitas+"', fakultas = '"+tFak+"', jurusan = '"+tJur+"', tahun_lulus = '"+tThnLulus+"', ipk = "+ipk+" where nik='" +tNik+ "'");
            }else                
            {
                stm.executeUpdate("INSERT into peserta VALUES('"+tNik+"','"+tKabAsal+"','"+tKabPen+"','"+tNamaLengkap+"','"+tTTL+"','"+sJenisKelamin+"','"+tAlamat+"','"+sAgama+"', '"+tNoTelp+"' ,'"+tEmaill+"','"+tUniversitas+"','"+tFak+"','"+tJur+"','"+tThnLulus+"',"+ipk+" )");                 
            }
            tblPeserta.setModel(new DefaultTableModel(dataTable,header));
            baca_data();
            aktif(false);
            setTombol(true);
        }catch(SQLException e) {
            JOptionPane.showMessageDialog(null, e);
            }
    }//GEN-LAST:event_cmdSimpanMouseClicked

