    private void setField()
    {
        int row = tblPeserta.getSelectedRow();
        txtNik.setText((String)tblPeserta.getValueAt(row, 0));
        txtKabAsal.setText((String)tblPeserta.getValueAt(row, 1));
        txtKabPenempatan.setText((String)tblPeserta.getValueAt(row, 2));
        txtNamaLengkap.setText((String)tblPeserta.getValueAt(row, 3));
        txtTTL.setText((String)tblPeserta.getValueAt(row, 4));
        cmbJenisKel.setSelectedItem((String)tblPeserta.getValueAt(row, 5));
        txtAlamat.setText((String)tblPeserta.getValueAt(row, 6));
        cmbAgama.setSelectedItem((String)tblPeserta.getValueAt(row, 7));
        txtNoTelp.setText((String)tblPeserta.getValueAt(row, 8));
        txtEmaill.setText((String)tblPeserta.getValueAt(row, 9));
        txtUniversitas.setText((String)tblPeserta.getValueAt(row, 10));
        txtFak.setText((String)tblPeserta.getValueAt(row, 11));
        txtJurusan.setText((String)tblPeserta.getValueAt(row, 12));
        txtThnLulus.setText((String)tblPeserta.getValueAt(row, 13));
        String ipkk = Double.toString((Double)tblPeserta.getValueAt(row, 14));
        txtIpk.setText(ipkk);
    }
    
    //method membuka database server, user, pass, database disesuaikan
    private void open_db()
    { try{
        KoneksiMysql kon = new KoneksiMysql("localhost","root","","uas_pkkp");
        Con = kon.getConnection();
    //System.out.println("Berhasil ");
    }catch (Exception e) {
        System.out.println("Error : "+e);
    }
    }
    
    private void baca_data()
    {
        try{
            stm = Con.createStatement(ResultSet.TYPE_SCROLL_SENSITIVE, ResultSet.CONCUR_READ_ONLY);
            RsPeserta = stm.executeQuery("SELECT * FROM peserta ORDER BY nik");
            
            ResultSetMetaData meta = RsPeserta.getMetaData();
            int col = meta.getColumnCount();
            int baris = 0;
            while(RsPeserta.next()) {
                baris = RsPeserta.getRow();
            }
            dataTable = new Object[baris][col];
            int x = 0;
            RsPeserta.beforeFirst();
            while(RsPeserta.next()) {
                dataTable[x][0] = RsPeserta.getString("nik");
                dataTable[x][1] = RsPeserta.getString("kab_asal");
                dataTable[x][2] = RsPeserta.getString("kab_penempatan");
                dataTable[x][3] = RsPeserta.getString("nama_lengkap");
                dataTable[x][4] = RsPeserta.getString("ttl");
                dataTable[x][5] = RsPeserta.getString("jenis_kelamin");
                dataTable[x][6] = RsPeserta.getString("alamat");
                dataTable[x][7] = RsPeserta.getString("agama");
                dataTable[x][8] = RsPeserta.getString("no_telp");
                dataTable[x][9] = RsPeserta.getString("email");
                dataTable[x][10] = RsPeserta.getString("universitas");
                dataTable[x][11] = RsPeserta.getString("fakultas");
                dataTable[x][12] = RsPeserta.getString("jurusan");
                dataTable[x][13] = RsPeserta.getString("tahun_lulus");
                dataTable[x][14] = RsPeserta.getDouble("ipk");
                x++;
            }
            tblPeserta.setModel(new DefaultTableModel(dataTable,header));
        }
        catch(SQLException e)
        {
            JOptionPane.showMessageDialog(null, e);
        }
    }
    
    //untuk mengkosongkan isian data
    private void kosong()
    {
        txtNik.setText("");
        txtKabAsal.setText("");
        txtKabPenempatan.setText("");
        txtNamaLengkap.setText("");
        txtTTL.setText("");
        txtAlamat.setText("");
        txtNoTelp.setText("");
        txtEmaill.setText("");
        txtUniversitas.setText("");
        txtFak.setText("");
        txtJurusan.setText("");
        txtThnLulus.setText("");
        txtIpk.setText("");
    }
    
    //mengset aktif tidak isian data
    private void aktif(boolean x)
    {
        txtNik.setEditable(x);
        txtKabAsal.setEditable(x);
        txtKabPenempatan.setEditable(x);
        txtNamaLengkap.setEditable(x);
        txtTTL.setEditable(x);
        cmbJenisKel.setEditable(x);
        txtAlamat.setEditable(x);
        cmbAgama.setEditable(x);
        txtNoTelp.setEditable(x);
        txtEmaill.setEditable(x);
        txtUniversitas.setEditable(x);
        txtFak.setEditable(x);
        txtJurusan.setEditable(x);
        txtThnLulus.setEditable(x);
        txtIpk.setEditable(x);
    }
    
    //mengset tombol on/off
    private void setTombol(boolean t)
    {
        cmdTambah.setEnabled(t);
        cmdEdit.setEnabled(t);
        cmdHapus.setEnabled(t);
        cmdSimpan.setEnabled(!t);
        cmdBatal.setEnabled(!t);
        cmdKeluar.setEnabled(t);
    }
    
    
    
