     private void open_db()
    {
        try{
        KoneksiMysql kon = new KoneksiMysql
        ("127.0.0.1","root","","uas_pkkp");
        Con = kon.getConnection();
        }
        catch (Exception e) {
        System.out.println("Error : "+e);
        }
    }
     
     private void baca_data()
    {
    open_db();
    try{
        stm = Con.createStatement();
        RsPes = stm.executeQuery("select * from administrasi where keterangan = 'LOLOS'");
        ResultSetMetaData meta = RsPes.getMetaData();
        int col = meta.getColumnCount();
        int baris = 0;
        while(RsPes.next()) {
            baris = RsPes.getRow();
        }
        dataTable = new Object[baris][col];
        int x = 0;
        RsPes.beforeFirst();
        while(RsPes.next()) {
            dataTable[x][0] = RsPes.getString("no_seleksi");
            dataTable[x][1] = RsPes.getString("nik");
            dataTable[x][2] = RsPes.getString("nama_lengkap");
            dataTable[x][3] = RsPes.getString("kab_asal");
            dataTable[x][4] = RsPes.getString("kab_penempatan");
            x++;
        }
        tblPeserta.setModel(new DefaultTableModel(dataTable,header));
    }
    catch(SQLException e)
    {
        JOptionPane.showMessageDialog(null, e);
    }
    }
     

