    private void tblPesertaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblPesertaMouseClicked
        // TODO add your handling code here:
        int row = tblPeserta.getSelectedRow();
        int tabelPeserta = tblPeserta.getSelectedRow();
        fWa.noSel = tblPeserta.getValueAt(tabelPeserta, 0).toString();
        fWa.nm = tblPeserta.getValueAt(tabelPeserta, 2).toString();
        fWa.pen = tblPeserta.getValueAt(tabelPeserta, 4).toString();
        fWa.pesertaTerpilih();
        this.dispose();   
    }//GEN-LAST:event_tblPesertaMouseClicked

